import React, { useState } from 'react';

const Controls = () => {
  const [params, setParams] = useState({
    populationSize: 100,
    generations: 1000,
    mutationRate: 0.01,
    crossoverRate: 0.8,
    tournamentSize: 5,
    vertices: 20,
    minWeight: 1,
    maxWeight: 100,
    edges: 190 // максимальна кількість ребер для повного графа
  });

  const [isRunning, setIsRunning] = useState(false);

  const handleParamChange = (e) => {
    const { name, value } = e.target;
    setParams(prev => ({
      ...prev,
      [name]: Number(value)
    }));
  };

  const handleStart = () => {
    setIsRunning(true);
    // TODO: Запустити алгоритм
  };

  const handleStop = () => {
    setIsRunning(false);
    // TODO: Зупинити алгоритм
  };

  const handlePause = () => {
    setIsRunning(false);
    // TODO: Поставити на паузу
  };

  return (
    <div className="bg-white shadow rounded-lg p-4">
      <h2 className="text-xl font-semibold mb-4">Керування алгоритмом</h2>
      
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Розмір популяції
          </label>
          <input
            type="number"
            name="populationSize"
            value={params.populationSize}
            onChange={handleParamChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            min="10"
            max="1000"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Кількість поколінь
          </label>
          <input
            type="number"
            name="generations"
            value={params.generations}
            onChange={handleParamChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            min="1"
            max="10000"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Шанс мутації
          </label>
          <input
            type="number"
            name="mutationRate"
            value={params.mutationRate}
            onChange={handleParamChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            min="0"
            max="1"
            step="0.01"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Шанс кросоверу
          </label>
          <input
            type="number"
            name="crossoverRate"
            value={params.crossoverRate}
            onChange={handleParamChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            min="0"
            max="1"
            step="0.01"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Розмір турніру
          </label>
          <input
            type="number"
            name="tournamentSize"
            value={params.tournamentSize}
            onChange={handleParamChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            min="2"
            max="20"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Кількість вершин
          </label>
          <input
            type="number"
            name="vertices"
            value={params.vertices}
            onChange={handleParamChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            min="3"
            max="100"
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Мін. вага
            </label>
            <input
              type="number"
              name="minWeight"
              value={params.minWeight}
              onChange={handleParamChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              min="1"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Макс. вага
            </label>
            <input
              type="number"
              name="maxWeight"
              value={params.maxWeight}
              onChange={handleParamChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              min={params.minWeight}
            />
          </div>
        </div>

        <div className="flex space-x-2">
          <button
            onClick={handleStart}
            disabled={isRunning}
            className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 disabled:opacity-50"
          >
            Старт
          </button>
          <button
            onClick={handlePause}
            disabled={!isRunning}
            className="flex-1 bg-yellow-600 text-white px-4 py-2 rounded-md hover:bg-yellow-700 disabled:opacity-50"
          >
            Пауза
          </button>
          <button
            onClick={handleStop}
            disabled={!isRunning}
            className="flex-1 bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 disabled:opacity-50"
          >
            Стоп
          </button>
        </div>
      </div>
    </div>
  );
};

export default Controls;
